CREATE TABLE IF NOT EXISTS `PREFIX_gsr_rating` (`RTG_ID` INT UNSIGNED NOT NULL auto_increment, `RTG_SHOP_ID` TINYINT UNSIGNED NOT NULL DEFAULT '1', `RTG_PROD_ID` INT UNSIGNED NOT NULL, `RTG_LANG_ID` TINYINT UNSIGNED NOT NULL DEFAULT '0', `RTG_NOTE` enum('1','2','3','4','5'), `RTG_STATUS` enum('0','1'), `RTG_CUST_ID` INT UNSIGNED NOT NULL, `RTG_DATE_ADD` timestamp DEFAULT CURRENT_TIMESTAMP, `RTG_DATA` text, PRIMARY KEY (`RTG_ID`), INDEX `X_BTR_RTG_CHECK` (`RTG_SHOP_ID`, `RTG_PROD_ID`, `RTG_CUST_ID`), INDEX `X_BTR_SHOP` (`RTG_SHOP_ID`), INDEX `X_BTR_STATUS` (`RTG_SHOP_ID`, `RTG_STATUS`)) ENGINE=InnoDB DEFAULT CHARSET=utf8;
CREATE TABLE IF NOT EXISTS `PREFIX_gsr_review` (`RVW_ID` INT UNSIGNED NOT NULL auto_increment, `RVW_SHOP_ID` TINYINT UNSIGNED NOT NULL DEFAULT '1', `RTG_ID` INT UNSIGNED NOT NULL, `RVW_PROD_ID` INT UNSIGNED NOT NULL, `RVW_LANG_ID` INT UNSIGNED NOT NULL, `RVW_DATE_ADD` timestamp NULL,  `RVW_DATE_UPD` timestamp DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP, `RVW_STATUS` enum('0','1'), `RVW_CUST_ID` INT UNSIGNED NOT NULL, `RVW_FB_POST_ID` BIGINT  UNSIGNED NOT NULL, `RVW_FB_PAGE_ID` BIGINT  UNSIGNED NOT NULL, `RVW_DATA` text, PRIMARY KEY (`RVW_ID`), INDEX `X_BTR_RTG_ID` (`RTG_ID`), INDEX `X_BTR_STATUS` (`RVW_SHOP_ID`, `RVW_STATUS`), INDEX `X_BTR_CUST` (`RVW_SHOP_ID`, `RVW_CUST_ID`), INDEX `X_BTR_FB_POST` (`RVW_FB_POST_ID`)) ENGINE=InnoDB DEFAULT CHARSET=utf8;
CREATE TABLE IF NOT EXISTS `PREFIX_gsr_callback` (`CBK_ID` INT UNSIGNED NOT NULL auto_increment, `CBK_SHOP_ID` TINYINT UNSIGNED NOT NULL DEFAULT '1', `ORDER_ID` INT UNSIGNED NOT NULL, `CBK_DATE_ADD` timestamp NOT NULL DEFAULT 0, `CBK_STATUS` enum('0','1'), `CBK_CUST_ID` INT UNSIGNED NOT NULL, `CBK_DATA` text, PRIMARY KEY (`CBK_ID`), INDEX `X_BTR_ORDER_ID` (`CBK_SHOP_ID`, `ORDER_ID`), INDEX `X_BTR_CBK_STATUS` (`CBK_SHOP_ID`, `CBK_STATUS`), INDEX `X_BTR_CBK_CUST_ID` (`CBK_SHOP_ID`, `CBK_CUST_ID`)) ENGINE=InnoDB DEFAULT CHARSET=utf8;
CREATE TABLE IF NOT EXISTS `PREFIX_gsr_customer` (`CUST_SHOP_ID` TINYINT UNSIGNED NOT NULL DEFAULT '1', `CUST_ID` INT UNSIGNED NOT NULL, `CUST_CBK_STATUS` enum('0','1'), PRIMARY KEY (`CUST_SHOP_ID`, `CUST_ID`), INDEX `X_BTR_CUST_STATUS` (`CUST_SHOP_ID`, `CUST_CBK_STATUS`)) ENGINE=InnoDB DEFAULT CHARSET=utf8;
CREATE TABLE IF NOT EXISTS `PREFIX_gsr_voucher` (`VCHR_ID` INT UNSIGNED NOT NULL auto_increment, `VCHR_SHOP_ID` TINYINT UNSIGNED NOT NULL DEFAULT '1', `VCHR_TYPE` VARCHAR(32) DEFAULT '', `VCHR_CUST_ID` INT UNSIGNED NOT NULL, `VCHR_COUNT` INT UNSIGNED NOT NULL, PRIMARY KEY (`VCHR_ID`), UNIQUE KEY `C_UNI_CUST_TYPE` (`VCHR_SHOP_ID`, `VCHR_TYPE`, `VCHR_CUST_ID`)) ENGINE=InnoDB DEFAULT CHARSET=utf8;
CREATE TABLE IF NOT EXISTS `PREFIX_gsr_review_report` (`RPT_RVW_ID` INT UNSIGNED NOT NULL DEFAULT '0', `RPT_CUST_ID` INT UNSIGNED NOT NULL, PRIMARY KEY (`RPT_RVW_ID`,`RPT_CUST_ID`)) ENGINE=InnoDB DEFAULT CHARSET=utf8;
CREATE TABLE IF NOT EXISTS `PREFIX_gsr_aftersales` (`AFS_ID` INT UNSIGNED NOT NULL, `AFS_TYPE` enum('rating','review') DEFAULT 'rating', `AFS_DATE_ADD` timestamp NULL,  `AFS_DATE_UPD` timestamp DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP, `AFS_DISPLAY` enum('0','1') DEFAULT '0', `AFS_DATA` text, PRIMARY KEY (`AFS_ID`, `AFS_TYPE`)) ENGINE=InnoDB DEFAULT CHARSET=utf8;
CREATE TABLE IF NOT EXISTS `PREFIX_gsr_orders_history` (`oh_id` INT UNSIGNED NOT NULL auto_increment, `oh_shop_id` TINYINT UNSIGNED NOT NULL DEFAULT '1', `oh_order_id` INT UNSIGNED NOT NULL, `oh_date_sent` timestamp NOT NULL DEFAULT 0, `oh_sent_count` INT UNSIGNED NOT NULL DEFAULT 0,  PRIMARY KEY (`oh_id`), UNIQUE KEY (`oh_shop_id`, `oh_order_id`)) ENGINE=InnoDB DEFAULT CHARSET=utf8;