ALTER TABLE `PREFIX_gsr_review` ADD `RVW_LANG_ID` TINYINT UNSIGNED NOT NULL DEFAULT '0' AFTER `RVW_PROD_ID`;
ALTER TABLE `PREFIX_gsr_review` MODIFY `RVW_DATE_ADD` timestamp NULL AFTER `RVW_LANG_ID`;
