CREATE TABLE IF NOT EXISTS `PREFIX_gsr_review_report` (`RPT_RVW_ID` INT UNSIGNED NOT NULL DEFAULT '0', `RPT_CUST_ID` INT UNSIGNED NOT NULL, PRIMARY KEY (`RPT_RVW_ID`,`RPT_CUST_ID`)) ENGINE=InnoDB DEFAULT CHARSET=utf8;